animation_js:
function setFontSizes(Wdth)
{
	scale=Wdth/1280;
   
   $('.animContainer1').css({'-webkit-transform': 'scale(' + ((Math.round((scale*100))/100)*(0.75)) + ',' + ((Math.round((scale*100))/100)*(0.75))+ ')rotate(0.1deg)'});
   $('.animContainer1').css({'-ms-transform': 'scale('+ ((Math.round((scale*100))/100)*(0.75)) + ',' + ((Math.round((scale*100))/100)*(0.75))+ ')rotate(0.1deg)'});
   $('.animContainer1').css({'transform': 'scale('+((Math.round((scale*100))/100)*(0.75)) + ',' + ((Math.round((scale*100))/100)*(0.75))+ ')rotate(0.1deg)'});
}	
function getDesc(title){
	setTimeout(function(){ 
		if(title==3){
		$('.animContainer1').show();
		}
		else{
		$('.animContainer1').hide();
		}
	}, 1);
$('.animContainer'+tempId).hide();
	tempId = title;
}

animation_css:
.contentHolder .contentDescription{width: 83%;height:  94%;left: 17%;position: absolute;float: right;top: 0;background-color: #F5F5F5 !important;}.animContainer1{position: absolute;top: 42.0%;left: 75.1%;z-index:0;display:none;}
.animDiv1 {background-image: url("../animations/Rotor.png");width:264px;height:278px;top: 0;left: 0;position: absolute;
    -webkit-animation: play1 2s steps(30, end) infinite;
       -moz-animation: play1 2s steps(30, end) infinite;
        -ms-animation: play1 2s steps(30, end) infinite;
         -o-animation: play1 2s steps(30, end) infinite;
            animation: play1 2s steps(30, end) infinite;
			
}
@-webkit-keyframes play1 {from {background-position:0px;} to {background-position:-7920px;}}
@-moz-keyframes play1    {from {background-position:0px;} to {background-position:-7920px;}}
@-ms-keyframes play1     {from {background-position:0px;} to {background-position:-7920px;}}
@-o-keyframes play1      {from {background-position:0px;} to {background-position:-7920px;}}
@keyframes play1         {from {background-position:0px;} to {background-position:-7920px;}}
