function getDesc(title){
	setTimeout(function(){ 
		if(title == 1 || title == 2 ){
				$('.image1').show();
				$('.image2').show();
				
			}
			else{
				$('.image1').hide();
				$('.image2').hide();
				
			}
		if(title == 3 || title == 4 ){
				$('.animC3').hide();
				stopAnimation1();
				startAnimation1();
				$('.animContainer1').show();
		}
			else{
				stopAnimation1();
				$('.animContainer1').hide();
		}
		if(title == 3 || title == 4 ){
				$('.image3').show();
				$('.image4').show();
				
			}
			else{
				$('.image3').hide();
				$('.image4').hide();
				
		}
}, 1);
/* 	$('.animContainer'+tempId).hide(); */
	tempId = title;
}
   var animationTimer1 = null;
   var animationTimer1_1 = null;
	var currentbgx1 = 0;
	var currentbgy1 = 0;
	var animDiv1 = null;
	var animDiv1_1 = null;
	function startAnimation1()
	{
	   
		if(animationTimer1 == null)
		{
			animationTimer1 = setInterval(moveAnimation1,50);
			animationTimer1_1 = animationTimer1;
		}
	}
	function stopAnimation1()
	{
		currentbgx1 = 0;
		currentbgy1 = 0;
		animDiv1_1 = document.getElementById("animDiv1");
		animDiv1_1.style.backgroundPosition = currentbgx1 + "px "+currentbgy1 + "px";
		clearInterval(animationTimer1_1);
		animationTimer1 = null;
	}
	function moveAnimation1()
	{
		if(animDiv1 == null)
		{
			animDiv1 = document.getElementById("animDiv1");
		}
		animDiv1.style.backgroundPosition = currentbgx1 + "px "+currentbgy1 + "px";
		currentbgx1 -= 444;
		if (currentbgx1 < -2664) {
			currentbgy1 -= 306;
			currentbgx1 = 0;
		}
		if (currentbgy1 < -2142 ) {
			currentbgy1 = 0;
			currentbgx1 = 0;
		}
	}

