function getDesc(title) {
    setTimeout(function() {
        
       if(title==4){
		    $('.animContainer3').hide();
			$('.animContainer4').hide();
		    $('.image3').hide();
			$('.image1').show();
			$('.image2').show();
			$('.animContainer2').show();
	}
	else if(title==5){
		    $('.image1').hide();
			$('.image2').hide();
            $('.animContainer2').hide();
			$('.animContainer4').hide();
		    $('.animContainer3').show();
		    $('.image3').show();
	}
	else if(title==7){
		    $('.image1').hide();
			$('.image2').hide();
			$('.image3').hide();
            $('.animContainer2').hide();
			$('.animContainer3').hide();
		    $('.animContainer4').show();
	}
	else{
		    $('.image1').hide();
			$('.image2').hide();
			$('.image3').hide();
            $('.animContainer2').hide();
			$('.animContainer3').hide();
			$('.animContainer4').hide();			
	}
    }, 1);
    
    /* 	$('.animContainer'+tempId).hide(); */   tempId = title;
}

