function getDesc(title){
	setTimeout(function(){ 
		if(title==0){
		$('.animContainer1').hide();
		$('.image1').show();
		$('.image2').show();
		$('.image3').show();
		$('.image4').show();
		}
		else if(title==2){
	    $('.image1').hide();
		$('.image2').hide();
		$('.image3').hide();
		$('.image4').hide();
		$('.animContainer1').show();
		}
		else{
		$('.image1').hide();
		$('.image2').hide();
		$('.image3').hide();
		$('.image4').hide();
		$('.animContainer1').hide();
		}
	}, 1);
$('.animContainer'+tempId).hide();
	tempId = title;
