 var setTime;
function getDesc(title){
	setTimeout(function(){ 
	if(title == 0){
		$('.animContainer1').show();
		$('.animContainer2').show();
	}else{
		clearTimeout(setTime);
		$('.animContainer1').hide();
		$('.animContainer2').hide();
	}
	if(title == 1){
	$('.text1').show();	
	}
	else{
	$('.text1').hide();	
	}
	if(title == 2){
	$('.text2').show();	
	}
	else{
	$('.text2').hide();	
	}
	if(title == 3){
	$('.text3').show();	
	}
	else{
	$('.text3').hide();	
	}
	if(title == 1 || title == 2 || title == 3){
