var animationTimer4 = null;
	var currentbgx4 = 0;
	var currentbgy4 = 0;
	var animDiv4 = null;
	function startAnimation4()
	{
		if(animationTimer4 == null)
		{
			currentbgy4 = 0;
			currentbgx4 = 0;
			animationTimer4 = setInterval(moveAnimation4,40);
		}
	}
	function stopAnimation4()
	{
		clearInterval(animationTimer4);
		animationTimer4 = null;
		currentbgy4 =0;
		currentbgx4 =0;
	}
	function moveAnimation4()
	{
		if(animDiv4 == null)
		{
			animDiv4 = document.getElementById("animDiv4");
		}
		animDiv4.style.backgroundPosition = currentbgx4 + "px "+currentbgy4+"px";
		currentbgx4 -= 333;
		if (currentbgx4 < -1665) {
			currentbgy4 -= 174;
			currentbgx4 = 0;
		}
		if (currentbgy4 < -2262 ) {
			currentbgy4 = -2262;
			currentbgx4 = -666;
		}
	}

