var animationTimer0 = null;
	var currentbgx0 = 0;
	var currentbgy0 = 0;
	var animDiv0 = null;
	function startAnimation0()
	{
		if(animationTimer0 == null)
		{
			currentbgy0 = 0;
			currentbgx0 = 0;
			animationTimer0 = setInterval(moveAnimation0,50);
		}
	}
	function stopAnimation0()
	{
		clearInterval(animationTimer0);
		animationTimer0 = null;
		currentbgy0 = 0;
		currentbgx0 = 0;
	}
	function moveAnimation0()
	{
		if(animDiv0 == null)
		{
			animDiv0 = document.getElementById("animDiv0");
		}
		animDiv0.style.backgroundPosition = currentbgx0 + "px "+currentbgy0+"px";
		currentbgx0 -= 226;
		if (currentbgx0 < -1808) {
			currentbgy0 -= 486;
			currentbgx0 = 0;
		}
		if (currentbgy0 < -2430 ) {
			currentbgy0 = 0;
			currentbgx0 = 0;
		}
	}

